<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Far_customer{    private $CI;    public function __construct()    {        $this->CI =& get_instance();        $this->CI->load->helper('url');        $this->CI->load->library('session');        $this->CI->load->database();    }    function is_customer_exists_by_mobile_number($mobile_number){        $query = $this->CI->db->query("SELECT * FROM customer_detail WHERE mobile_number='".$mobile_number."'");        if($query->num_rows() > 0) {            return true;        }        return false;    }    function get_customer_detail($customer_id){        $customer_detail = array();        $query = $this->CI->db->query("SELECT * FROM customer_detail WHERE customer_id='".$customer_id."'");        if($query->num_rows() > 0) {            $customer_detail = $query->row_array();        }        return $customer_detail;    }    function list_all_customer_simple(){        $list_all_customer_simple = array();        $query = $this->CI->db->query("SELECT * FROM `view_customer_list` ORDER BY fullname ASC");        if($query->num_rows() > 0) {            $list_all_customer_simple = $query->result_array();        }        return $list_all_customer_simple;    }    function add_new_customer($customer_data){        $this->CI->db->insert('customer_detail', $customer_data);        $wp_customer_id = $this->CI->db->insert_id();        return $wp_customer_id;    }    function update_customer_detail($key, $key_value, $column, $value){        $data = array(            $column => $value        );        $this->CI->db->where($key, $key_value);        $this->CI->db->update('customer_detail', $data);    }}